package com.example.fxproj;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.scene.control.Button;import javafx.scene.control.Label;import javafx.scene.image.Image;import javafx.scene.image.ImageView;public class OtherLabs {    @FXML    private ImageView imageView;    @FXML    private Button changeButton;    @FXML    private Label text;    Image image = new Image(getClass().getResourceAsStream("бідося.jpg"));    Image image2 = new Image(getClass().getResourceAsStream("завантаження.jpg"));    @FXML    void changeImage(){        if(imageView.getImage() == image){            imageView.setImage(image2);        } else{            imageView.setImage(image);        }    }}